using System; using System.Collections.Generic;  namespace CryptoAccouting {     public class CryptoAccoutingCore     {          //private List<Instrument> assets;         public string BaseCurrency { get; set; }         private Balance myBalance;          // Test Data         private Instrument coin1, coin2, coin3;// Test Data         private Position pos1, pos2, pos3; //Test Data         //          public CryptoAccoutingCore(string baseCur)         {
            myBalance = new Balance();             BaseCurrency = baseCur;         }          public Balance fetchMyBalance()         {
            // Test Data Creation
            coin1 = new Instrument() { Symbol = "BTC", LogoFileName = "Images/btc.png" };             coin2 = new Instrument() { Symbol = "ETH", LogoFileName = "Images/eth.png" };             coin3 = new Instrument() { Symbol = "REP", LogoFileName = "Images/rep.png" };
            pos1 = new Position(coin1, "1") { Amount = 850 };             pos2 = new Position(coin2, "2") { Amount = 1000 };             pos3 = new Position(coin3, "3") { Amount = 25000 };             pos1.AttachPriceData(new Price(coin1) { LatestPrice = 780, DayVolume=100000 });             pos2.AttachPriceData(new Price(coin2) { LatestPrice = 0.14, DayVolume = 30000 });             pos3.AttachPriceData(new Price(coin3) { LatestPrice = 0.013, DayVolume = 45560 });                              myBalance.AttachPosition(pos1);             myBalance.AttachPosition(pos2);             myBalance.AttachPosition(pos3);              return myBalance;         }     } } 